import React from 'react';
import './ProjMinCard.css';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { yellow } from '@material-ui/core/colors';

const useStyles = makeStyles({
  card: {
    minWidth: 375,
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 17,
  },
  pos: {
    marginBottom: 12,
  },
  flexer: {
    color: 'yellow',
  },
});

export default function ProjMinCard(props) {
  const classes = useStyles();
  let toolsUsed = "";
  for (let i = 0; i < props.tools.length; i++) {
    toolsUsed += props.tools[i];
    if (i < props.tools.length - 1) {
      toolsUsed += ", ";
    }
  }

  if (props.taken === "0") console.log("Not Taken");
  else console.log("Taken");

  return (
    <Card className={classes.card}>
      <CardContent>

        <div className={classes.flexer}>
          <div className={classes.flexer}>
            as
            <Typography variant="h4" component="h2">
              ProjecsTitle
            </Typography>
          </div>
          <div className={classes.flexer}>
            <img alt="tick-logo" src={require('../../assets/icons/check_circle_black_18x18.png')} />
          </div>
        </div>
        <br />
        <Typography variant="body2" component="p">
          In this project you will need to do so and so....
        </Typography>

        <Typography variant="body2" component="p">
          Tools Used: <b>{toolsUsed}</b>
        </Typography>


      </CardContent>
      <CardActions>
        <Button>Read More</Button>
        <Typography>Applicants: {props.applicants}</Typography>
      </CardActions>
    </Card>
  );
}
