import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles({
  card: {
    minWidth: 375,
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 17,
  },
  pos: {
    marginBottom: 12,
  },
});

export default function OrgCard(props) {
  const classes = useStyles();
  let toolsUsed = "";
  for(let i = 0; i<props.tools.length; i++){
    toolsUsed += props.tools[i];
    if(i < props.tools.length-1){
      toolsUsed += ", ";
    }
  }

  if(props.taken === "0") console.log("Not Taken");
  else console.log("Taken");

  return (
    <Card className={classes.card}>
      <CardContent>
        <Typography variant="h4" component="h2">
          Project Title
        </Typography>
        <br />
        <Typography variant="body2" component="p">
          In this project you will need to do so and so....
        </Typography>

        <Typography variant="body2" component="p">
          Tools Used: <b>{toolsUsed}</b>
        </Typography>


      </CardContent>
      <CardActions>
        <Button>Read More</Button>
        <Typography>Applicants: {props.applicants}</Typography>
      </CardActions>
    </Card>
  );
}
